try {  // set up Composer auto-loader  $loader = require_once (__DIR__.'/vendor/autoload.php');  // initialize PHPCR session to Magnolia's 'website' repository  $prm = array(    'jackalope.jackrabbit_uri' => 'http://localhost:8080/magnoliaAuthor/.davex/'  );  $creds = new \PHPCR\SimpleCredentials('superuser', 'superuser');  $repository = \Jackalope\RepositoryFactoryJackrabbit::getRepository($prm);  $website = $repository->login($creds, 'website');  // get a node from the repository using its UUID and read a property from it  // note: this UUID might change in future releases of Magnolia CMS  $uuid = '674469eb-31c2-4dc3-aaa9-935469790539';  $node = $website->getNodeByIdentifier($uuid);  $abstract = $node->getPropertyValue('title');  echo $abstract;  // clean up  // clean up  unset($website);} catch(Exception $e) {  echo $e->getTraceAsString();}
