try {  // set up Composer autoloader  $loader = require_once (__DIR__.'/vendor/autoload.php');  // initialize PHPCR session to Magnolia's 'dms' repository  $prm = array(    'jackalope.jackrabbit_uri' => 'http://localhost:8080/magnoliaAuthor/.davex/'  );  $creds = new \PHPCR\SimpleCredentials('superuser', 'superuser');  $repository = \Jackalope\RepositoryFactoryJackrabbit::getRepository($prm);  $website = $repository->login($creds, 'website');  // get a node from the repository and read all properties from it  $node = $website->getNode('/demo-project/about/history');  $properties = $node->getPropertiesValues();  print_r($properties);  // clean up  // clean up  unset($website);} catch(Exception $e) {  echo $e->getTraceAsString();}
