try {  // set up autoloader  $loader = require_once (__DIR__.'/vendor/autoload.php');  // initialize PHPCR session to Magnolia's 'dms' repository  $prm = array(    'jackalope.jackrabbit_uri' => 'http://localhost:8080/magnoliaAuthor/.davex/'  );  $creds = new \PHPCR\SimpleCredentials('superuser', 'superuser');  $repository = \Jackalope\RepositoryFactoryJackrabbit::getRepository($prm);  $website = $repository->login($creds, 'website');  // get a node from the repository  $node = $website->getNode('/demo-project/about/history');  // create a new property on it  $node->setProperty('foo', 'bar');  // also modify an existing property  $node->setProperty('title', 'Company History');  // save changes  $website->save();  // clean up  // clean up  unset($website);} catch(Exception $e) {  echo $e->getTraceAsString();}
