try {  // set up autoloader  $loader = require_once (__DIR__.'/vendor/autoload.php');  // initialize PHPCR session to Magnolia's 'dms' repository  $prm = array(    'jackalope.jackrabbit_uri' => 'http://localhost:8080/magnoliaAuthor/.davex/'  );  $creds = new \PHPCR\SimpleCredentials('superuser', 'superuser');  $repository = \Jackalope\RepositoryFactoryJackrabbit::getRepository($prm);  $website = $repository->login($creds, 'website');  // iterate over the nodes which have abstracts and print them  $node = $website->getNode('/demo-project/about/subsection-articles');  foreach ($node->getNodes() as $child) {    if ($child->hasProperty('abstract')) {      $abstract = $node->getProperty('abstract');      echo "<h1>" . $node->getPath() . '</h1>';      echo $abstract->getValue();    }  }  // clean up  // clean up  unset($website);} catch(Exception $e) {  echo $e->getTraceAsString();}
