class SampleRemoteApiTest extends PHPUnit_Framework_TestCase {  public function testLoadUser()    $client  = new Guzzle\Http\Client('https://base.url.com');    $request  = array(      'type'    => 'rpc',      'tid'    => 1,      'action'  => 'ControllerUser',      'method'  => 'load',      'data'    => array(4711)      );    $request  = $client->post(      'router.php',       json_encode($request),       array(        'X-IN-TESTING'    => true,        'X-Requested-With'  => 'XMLHttpRequest',        'Content-Type'    => 'application/json'      )    );        $response  = $request->send();    $body    = json_decode( $response->getBody(), true );    $result    = $body['result'];      $this->assertEquals( true, $result['success'] );    $this->assertEquals( 4711, $result['data']['user_id'] );  }}