// Implementierung des hook_message_broker_consumersfunction mymodule_message_broker_consumers($self_name) {  $consumers = array();  // Consumer für Queue Shopsystem1 registrieren  $consumers['shop-product-updates'] = array(    'queue' => 'Shopsystem1',     'callback' => 'mymodule_handle_product_updates');  return $consumers;}// Hier findet die Nachrichtenverarbeitung stattfunction mymodule_handle_product_updates($message_body, $ack) {  $messageData = json_decode($message_body);  // Nachricht validieren, ggf. InvalidMessageException werfen  // Nachricht dann verarbeiten und am Ende bestätigen:  $ack();}