// HOSTNAME, USERNAME, PASSWORD und DATENBANK sind Konstanten 
// mit den entsprechenden Werten// 1. Schritt: Verbindung zur MySQL-Datenbank aufbauen$connection = mysqli_connect(HOSTNAME, USERNAME, PASSWORD, DATENBANK);if (!$connection) {  // Keine Verbindung zu MySQL}else {  // 2. Schritt: Die Abfrage als Prepared Statement vorbereiten:  $statement = mysqli_prepare($connection, "INSERT INTO text_tabelle (Titel, Text) values (?, ?)");  // 3. Schritt: Die Parameter daran binden:  mysqli_stmt_bind_param($statement, 'ss', $titel, $text);  // 4. Schritt: Die Parameter füllen:  $titel = htmlentities(strip_tags($_POST["titel"]));  $text  = strip_tags($_POST["text"], "<a><img><pre><p><b>");  // 5. Schritt: Das so vorbereitete Statement kann nun ausgeführt werden:  $ok = mysqli_stmt_execute($statement);  if (!ok) {    // MySQL-Fehler  }  else {    // Text erfolgreich eingetragen  }  // Das Statement kann geschlossen werden:  mysqli_stmt_close($statement);  // Die Verbindung kann geschlossen werden:  mysqli_close($connection);}