function lmbWfl_1($wfl_inst, $active_task, $gtabid = null, $ID = null, $params = null) {  global $gwfl;  global $session;  // Get task parameters from admin interface  $t_params = $gwfl[WFL_ID]['task']['params'];  // Get history of this workflow instance  $hist = lmb_wfl_getHistory(WFL_ID, $wfl_inst);  $days = $t_params['reminder_days'];    if ($params['showElements']) {    myExt_showElements($hist);  } else {    if (!in_array(1, $hist["task_id"])) {      // Create reminder to contact customer      if (!myExt_createReminder(lmb_utf8_decode("Kunden kontaktieren"), $days, $gtabid, $ID, $wfl_inst))      return false;      // Create entry in correspondance table      if (!myExt_wflAddCorrespondence($ID, lmb_utf8_decode("Broschüre"), lmb_utf8_decode("Broschüre wurde an den Kunden gesendet.")))      return false;      // Set acquisition workflow status      if (!update_data(array("$gtabid," . ELEMENT_ID_ACQUISITION_STATUS . ",$ID" => lmb_utf8_decode("Broschüre zugesandt"))))      return false;      return 1;    }    return true;  }}